var GrassHopper = require('gh-core');

var AppsAPI = require('gh-apps');

/**
 * @REST getApps
 *
 * Get all apps in a tenant
 *
 * @Server      admin
 * @Method      GET
 * @Path        /apps
 * @QueryParam  {number}            tenantId        The id of the tenant to retrieve the apps for
 * @Return      {App[]}                             All available apps in the tenant
 */
GrassHopper.globalAdminRouter.on('get', '/api/apps', function(req, res) {
    AppsAPI.getApps(req.ctx, req.query.tenantId, function(err, apps) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        res.status(200).send(apps);
    });
});

/**
 * @REST getApp
 *
 * Get an app
 *
 * @Server      admin
 * @Method      GET
 * @Path        /apps/{id}
 * @PathParam   {number}            id              The id of the app to retrieve
 * @Return      {App}                               The requested app
 */
GrassHopper.globalAdminRouter.on('get', '/api/apps/:id', function(req, res) {
    AppsAPI.getApp(req.ctx, req.params.id, function(err, app) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        res.status(200).send(app);
    });
});

/**
 * @REST getAppAdmins
 *
 * Get the administrators for an app
 *
 * @Server      admin,app
 * @Method      GET
 * @Path        /apps/{id}/admins
 * @PathParam   {number}            id              The id of the app to retrieve the administrators for
 * @QueryParam  {number}            [limit]         The maximum number of results to retrieve. Default: 10
 * @QueryParam  {number}            [offset]        The paging number of the results to retrieve
 * @Return      {UserList}                          The administrators for the app
 */
var _getAppAdmins = function(req, res) {
    AppsAPI.getAppAdmins(req.ctx, req.params.id, req.query.limit, req.query.offset, function(err, appAdmins) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        res.status(200).send(appAdmins);
    });
};

GrassHopper.globalAdminRouter.on('get', '/api/apps/:id/admins', _getAppAdmins);
GrassHopper.appRouter.on('get', '/api/apps/:id/admins', _getAppAdmins);

/**
 * @REST createApp
 *
 * Create a new app
 *
 * @Server      admin
 * @Method      POST
 * @Path        /apps
 * @FormParam   {string}            displayName     The name of the app (e.g. `Timetable 2013-14`)
 * @FormParam   {string}            host            The host on which the app can be found
 * @FormParam   {number}            tenantId        The id of the tenant on which to create the app
 * @FormParam   {string}            type            The type of the app     [timetable]
 * @Return      {App}                               The created app
 */
GrassHopper.globalAdminRouter.on('post', '/api/apps', function(req, res) {
    AppsAPI.createApp(req.ctx, req.body.tenantId, req.body.displayName, req.body.host, req.body.type, function(err, app) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        res.status(201).send(app);
    });
});

/**
 * @REST updateApp
 *
 * Update an app
 *
 * @Server      admin,app
 * @Method      POST
 * @Path        /apps/{id}
 * @PathParam   {number}            id              The id of the app to update
 * @FormParam   {string}            [displayName]   Updated app name
 * @FormParam   {boolean}           [enabled]       Whether the app should be enabled
 * @FormParam   {string}            [host]          Updated app host
 * @Return      {App}                               The updated app
 */
var _updateApp = function(req, res) {
    AppsAPI.updateApp(req.ctx, req.params.id, req.body, function(err, app) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        res.status(200).send(app);
    });
};

GrassHopper.globalAdminRouter.on('post', '/api/apps/:id', _updateApp);
GrassHopper.appRouter.on('post', '/api/apps/:id', _updateApp);

/**
 * @REST updateAppAdmins
 *
 * Update the administrators of an app
 *
 * @Server      admin,app
 * @Method      POST
 * @Path        /apps/{id}/admins
 * @PathParam   {number}            id              The id of the app for which to update the administrators
 * @BodyParam   {AppAdminsUpdate}   body            Object that describes the app administrator changes to apply
 * @Return      {void}
 */
var _updateAppAdmins = function(req, res) {
    AppsAPI.updateAppAdmins(req.ctx, req.params.id, req.body, function(err) {
        if (err) {
            return res.status(err.code).send(err.msg);
        }

        res.sendStatus(200);
    });
};

GrassHopper.globalAdminRouter.on('post', '/api/apps/:id/admins', _updateAppAdmins);
GrassHopper.appRouter.on('post', '/api/apps/:id/admins', _updateAppAdmins);
