var GrassHopper = require('gh-core');

var DocsAPI = require('gh-docs');

/**
 * @REST getSwaggerDocumentation
 *
 * Get the Swagger documentation resources
 *
 * @Api private
 * @Server      admin,app
 * @Method      GET
 * @Path        /swagger
 * @Return      {object}                  Swagger resource listing @see https://github.com/wordnik/swagger-spec/blob/master/versions/1.2.md#51-resource-listing
 */
var _getSwaggerDocumentation = function(req, res) {
    return res.status(200).send(DocsAPI.getResources(req.ctx));
};

GrassHopper.globalAdminRouter.on('get', '/api/swagger', _getSwaggerDocumentation);
GrassHopper.appRouter.on('get', '/api/swagger', _getSwaggerDocumentation);

/**
 * @REST getSwaggerApiDocumentation
 *
 * Get the Swagger documentation for an API
 *
 * @Api private
 * @Server      admin,app
 * @Method      GET
 * @Path        /swagger/{id}
 * @PathParam   {string}     id           The API id to get the Swagger documentation for
 * @Return      {object}                  Swagger API declaration @see https://github.com/wordnik/swagger-spec/blob/master/versions/1.2.md#52-api-declaration
 */
var _getSwaggerApiDocumentation = function(req, res) {
    return res.status(200).send(DocsAPI.getApi(req.ctx, req.params.id));
};

GrassHopper.globalAdminRouter.on('get', '/api/swagger/:id', _getSwaggerApiDocumentation);
GrassHopper.appRouter.on('get', '/api/swagger/:id', _getSwaggerApiDocumentation);
